import React, { Component } from "react";
import axios from "axios";
import '../../components/common/2columns_layout.css';
import '../../components/common/2columns_layout.css';
import '../../components/common/PlaceBox.css';
import '../../pages/TopProvince/TopProvince.css';
import Navbar from '../../components/common/navbarPage.js';
// import NextButton from '/Users/j.kanlaya/Downloads/Mike/website/geovisualization/src/components/common/NextButton.js';
import {Link} from 'react-router-dom';
import arrow from '../../images/TopProvince/12_arrow-right.png';
import styled from 'styled-components';

const StyledButton = styled(Link)`
    width: 60px;
    height: 100%;
    background-color: #5863F8;
    color: white;
    border: 0;
    display: flex;
    justify-content: center;
    align-items: center;
    background-image: url(${arrow});
    background-repeat: no-repeat;
    background-position: center;
    cursor: pointer;
`

export default class TopProvince extends Component {
  state = {
    province: [],
    send_data: '',
    mapShow: false,
  }

  changeState = () => {   
    this.setState({
      send_data: this.state.province.provinceTh,
      mapShow: false
    });
  };  

  componentDidMount() {
    axios.get(`http://localhost:30011/getRankProvince`)
      .then(res => {
        const province = res.data;
        this.setState({ province });
      })
  }

  render() {
    return (
      <div class="top layout placeBox">
          <Navbar />
          <div class="BGimg" />
          <div class="LayOut">
              <div class="PageTitle">Popular Province</div>
              <div class="PageBreadCrumbs">Home&emsp;|&emsp;Popular Province</div>
              <div class="Title">Top 10  Provinces <br /> in Thailand</div>
              <div class="row">
{/* Left column */}
                  <div class="column">
                      <div class="container">
                          <div class="Picture01" />
                          <div class="TextBox">
                              <div class="RankTop">1 ST</div>
                              {/* <div class="PlaceName">น่าน</div>
                              <div class="PlaceDesc">แข่งเรือลือเลื่อง เมืองงาช้างดำ จิตรกรรมวัดภูมินทร์ แดนดินส้มสีทอง เรืองรองพระธาตุแช่แห้ง</div> */}

                                {this.state.province.map(data => data.provinceRank===1? 
                                  <>
                                    <div class="PlaceName">{data.provinceTh}</div>
                                    <div class="PlaceDesc">{data.provinceDesc}</div>
                                  </>
                                  :null)}
                          </div>
                          {/* <NextButton onClick={this.changeState}/> */}
                          {this.state.province.map(data => data.provinceRank===1 ? 
                            // <StyledButton to="/top1Province" onClick={this.changeState} send_data={data.provinceTh}/>
                            <StyledButton 
                              // to="/top1Province" 
                              // component="Hello World"
                              // onClick={this.changeState} 
                              // send_data="จังหวัดจ้าาา"/>

                              to={{
                                pathname: "/top1Province",
                                sampleParam: "Hello", 
                                sampleParam2: "World!"
                                // state: {
                                //     hello: "Hello World"
                                // }
                              }}
                            />
                            : null
                          )}
                          {/* <StyledButton 
                            to={{pathname: '/top1Province', info: "djaaa"}}
                          /> */}
                          {/* <button to="top1Province" pinfo="จังหวัดจ้าา" /> */}

                      </div>
                      <div class="container">
                          <div class="Picture03" />
                          <div class="TextBox">
                              <div class="RankTop">3 RD</div>
                              {/* <div class="PlaceName">ปราจีนบุรี</div>
                              <div class="PlaceDesc">ศรีมหาโพธิ์คู่บ้าน ไผ่ตงหวานคู่เมือง ผลไม้ลือเลื่อง เขตเมืองทวารวดี</div> */}
                              {this.state.province.map(data => data.provinceRank===3? 
                                  <>
                                    <div class="PlaceName">{data.provinceTh}</div>
                                    <div class="PlaceDesc">{data.provinceDesc}</div>
                                  </>
                                  :null)}
                          </div>
                          {this.state.province.map(data => data.provinceRank===3 ? 
                            <StyledButton to="/top3Province" onClick={this.changeState} send_data={data.provinceTh}/>
                            : null
                          )}
                      </div>
                      <div class="container">
                          <div class="Picture05" />
                          <div class="TextBox">
                              <div class="RankReg">5 TH</div>
                              {/* <div class="PlaceName">ขอนแก่น</div>
                              <div class="PlaceDesc">พระธาตุขามแก่น เสียงแคนดอกคูน ศูนย์รวมผ้าไหม ร่วมใจผูกเสี่ยว เที่ยวขอนแก่นนครใหญ่ ไดโนเสาร์สิรินธรเน่ สุดเท่เหรียญทองแรกมวยโอลิมปิก</div> */}
                              {this.state.province.map(data => data.provinceRank===5? 
                                  <>
                                    <div class="PlaceName">{data.provinceTh}</div>
                                    <div class="PlaceDesc">{data.provinceDesc}</div>
                                  </>
                                  :null)}
                          </div>
                          {this.state.province.map(data => data.provinceRank===5 ? 
                            <StyledButton to="/top5Province" onClick={this.changeState} send_data={data.provinceTh}/>
                            : null
                          )}
                      </div>
                      <div class="container">
                          <div class="Picture07" />
                          <div class="TextBox">
                              <div class="RankReg">7 TH</div>
                              {/* <div class="PlaceName">นครนายก</div>
                              <div class="PlaceDesc">เมืองในฝันที่ใกล้กรุง ภูเขางาม น้ำตกสวย รวยธรรมชาติ ปราศจากมลพิษ</div> */}

                              {this.state.province.map(data => data.provinceRank===7? 
                                  <>
                                    <div class="PlaceName">{data.provinceTh}</div>
                                    <div class="PlaceDesc">{data.provinceDesc}</div>
                                  </>
                                  :null)}
                          </div>
                          {this.state.province.map(data => data.provinceRank===7 ? 
                            <StyledButton to="/top7Province" onClick={this.changeState} send_data={data.provinceTh}/>
                            : null
                          )}
                      </div>
                      <div class="container">
                          <div class="Picture09" />
                          <div class="TextBox">
                              <div class="RankReg">9 TH</div>
                              {/* <div class="PlaceName">ตรัง</div>
                              <div class="PlaceDesc">เมืองพระยารัษฎา ชาวประชาใจกว้าง หมูย่างรสเลิศ ถิ่นกำเนิดยางพารา เด่นสง่าดอกศรีตรัง ปะการังใต้ทะเล เสน่ห์หาดทรายงาม น้ำตกสวยตระการตา</div> */}

                              {this.state.province.map(data => data.provinceRank===9? 
                                  <>
                                    <div class="PlaceName">{data.provinceTh}</div>
                                    <div class="PlaceDesc">{data.provinceDesc}</div>
                                  </>
                                  :null)}
                          </div>
                          {this.state.province.map(data => data.provinceRank===9 ? 
                            <StyledButton to="/top9Province" onClick={this.changeState} send_data={data.provinceTh}/>
                            : null
                          )}
                      </div>
{/* Right column */}
                  </div>
                  <div class="column">
                      <div class="container">
                          <div class="Picture02" />
                          <div class="TextBox">
                              <div class="RankTop">2 ND</div>
                              {/* <div class="PlaceName">สมุทรสงคราม</div>
                              <div class="PlaceDesc">เมืองหอยหลอด ยอดลิ้นจี่ มีอุทยาน ร.2 แม่กลองไหลผ่าน นมัสการหลวงพ่อบ้านแหลม</div> */}

                              {this.state.province.map(data => data.provinceRank===2? 
                                  <>
                                    <div class="PlaceName">{data.provinceTh}</div>
                                    <div class="PlaceDesc">{data.provinceDesc}</div>
                                  </>
                                  :null)}
                          </div>
                          {this.state.province.map(data => data.provinceRank===2 ? 
                            <StyledButton to="/top1Province" onClick={this.changeState} send_data={data.provinceTh}/>
                            : null
                          )}
                      </div>
                      <div class="container">
                          <div class="Picture04" />
                          <div class="TextBox">
                              <div class="RankReg">4 TH</div>
                              {/* <div class="PlaceName">ระยอง</div>
                              <div class="PlaceDesc">ผลไม้รสล้ำ อุตสาหกรรมก้าวหน้า น้ำปลารสเด็ด เกาะเสม็ดสวยหรู สุนทรภู่กวีเอก</div> */}

                              {this.state.province.map(data => data.provinceRank===4? 
                                  <>
                                    <div class="PlaceName">{data.provinceTh}</div>
                                    <div class="PlaceDesc">{data.provinceDesc}</div>
                                  </>
                                  :null
                              )}
                          </div>
                          {this.state.province.map(data => data.provinceRank===4 ? 
                            <StyledButton to="/top1Province" onClick={this.changeState} send_data={data.provinceTh}/>
                            : null
                          )}
                      </div>
                      <div class="container">
                          <div class="Picture06" />
                          <div class="TextBox">
                              <div class="RankReg">6 TH</div>
                              {/* <div class="PlaceName">ราชบุรี</div>
                              <div class="PlaceDesc">คนสวยโพธาราม คนงามบ้านโป่ง เมืองโอ่งมังกร วัดขนอนหนังใหญ่ ตื่นใจถ้ำงาม ตลาดน้ำดำเนิน เพลินค้างคาวร้อยล้าน ย่านยี่สกปลาดี</div> */}

                              {this.state.province.map(data => data.provinceRank===6? 
                                  <>
                                    <div class="PlaceName">{data.provinceTh}</div>
                                    <div class="PlaceDesc">{data.provinceDesc}</div>
                                  </>
                                  :null)}
                          </div>
                          {this.state.province.map(data => data.provinceRank===6 ? 
                            <StyledButton to="/top1Province" onClick={this.changeState} send_data={data.provinceTh}/>
                            : null
                          )}
                      </div>
                      <div class="container">
                          <div class="Picture08" />
                          <div class="TextBox">
                              <div class="RankReg">8 TH</div>
                              {/* <div class="PlaceName">จันทบุรี</div>
                              <div class="PlaceDesc">น้ำตกลือเลื่อง เมืองผลไม้ พริกไทยพันธุ์ดี อัญมณีมากเหลือ เสื่อจันทบูร สมบูรณ์ธรรมชาติ สมเด็จพระเจ้าตากสินมหาราช รวมญาติกู้ชาติที่จันทบุรี</div> */}

                              {this.state.province.map(data => data.provinceRank===8? 
                                  <>
                                    <div class="PlaceName">{data.provinceTh}</div>
                                    <div class="PlaceDesc">{data.provinceDesc}</div>
                                  </>
                                  :null)}
                          </div>
                          {this.state.province.map(data => data.provinceRank===8 ? 
                            <StyledButton to="/top1Province" onClick={this.changeState} send_data={data.provinceTh}/>
                            : null
                          )}
                      </div>
                      <div class="container">
                          <div class="Picture10" />
                          <div class="TextBox">
                              <div class="RankReg">10 TH</div>
                              {/* <div class="PlaceName">พะเยา</div>
                              <div class="PlaceDesc">กว๊านพะเยาแหล่งชีวิต ศักดิ์สิทธิ์พระเจ้าตนหลวง บวงสรวงพ่อขุนงำเมือง งามลือเลื่องดอยบุษราคัม</div> */}

                              {this.state.province.map(data => data.provinceRank===10? 
                                  <>
                                    <div class="PlaceName">{data.provinceTh}</div>
                                    <div class="PlaceDesc">{data.provinceDesc}</div>
                                  </>
                                  :null)}
                          </div>
                          {this.state.province.map(data => data.provinceRank===10 ? 
                            <StyledButton to="/top1Province" onClick={this.changeState} send_data={data.provinceTh}/>
                            : null
                          )}
                      </div>
                  </div>
              </div>
          </div>
      </div>
    );
  }
}
