// ============================= Google Maps TEST =============================
// AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM

import React, { Component } from 'react';
import { compose } from "recompose"
import { withScriptjs, withGoogleMap, Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import axios from "axios";
import NavbarMaps from '../../components/common/navbarMaps';
import './maps.css'

const mapStyles = {
    top: '0',
    buttom: '0',
    width: 'auto',
    height: 'auto',
    left: '300px',
};
class MapContainer extends Component {    
    state = {
        showingInfoWindow: false,
        activeMarker: {},
        selectedPlace: {},
        provinceResponse:[],        
        placeResponse:[],
        placeTarget:[],
        coor:{},
        clickedPlace: '',
        targetProvince: '',
        threadId:'',
        threadTitle: '',

    };

    componentDidMount() {
        axios.get(`http://localhost:30011/getRankProvince`, {
            params: {
                provinceRank: 1
            }
        })
            .then(res => {
                const provinceResponse = res.data[0];
                this.setState({ provinceResponse });
                this.setState({ targetProvince: this.state.provinceResponse.provinceTh })
                console.log(this.state.targetProvince);
        })

        axios.get(`http://localhost:30011/getThreadData`, {
            params: {
                // provinceTh: this.state.targetProvince
                provinceTh: "สมุทรสงคราม"
            }
        })
        .then(res => {
            this.setState({ placeResponse: res.data });
            console.log(this.state.placeResponse);
            // const latLon = this.state.placeResponse.map(row => {
            //     return row.coor
            // })
            // this.setState({coor: latLon})
            // console.log(this.state.coor);



        })  
    }

    onMarkerClick = (props, marker, e) =>
    this.setState({
        selectedPlace: props,
        activeMarker: marker,
        showingInfoWindow: true,
        clickedPlace: props.name,
    });

    onMapClicked = (props) => {
        if (this.state.showingInfoWindow) {
            this.setState({
                showingInfoWindow: false,
                activeMarker: null
            })
        }
    };

    // renderMarkers() {
    //     return this.state.coor.map( i => {
    //         return i.map( d => {
    //             return <Marker
    //                 key={ d }
    //                 // onClick = { this.onMarkerClick }
    //                 // title = { location.locName }
    //                 // position = { JSON.parse(location.position) }
    //                 // desc = { location.desc }
    //                 // animation = { this.state.animation[i] }
    //                 // name = { location.locName } />
    //                 onClick={this.onMarkerClick}
    //                 name={d.placeName}
    //                 position={{lat: d.lat, lng: d.lon}} />
    //             })
    //     })
    // }

    // renderMarkers() {
    //     return this.state.coor.map( i => {
    //         return <Marker
    //             key={ i }
    //             // onClick = { this.onMarkerClick }
    //             // title = { location.locName }
    //             // position = { JSON.parse(location.position) }
    //             // desc = { location.desc }
    //             // animation = { this.state.animation[i] }
    //             // name = { location.locName } />
    //             onClick={this.onMarkerClick}
    //             name={i.placeName}
    //             position={{lat: i.lat, lng: i.lon}} />
    //         })
    // }


    render() {
        return (  
            <div class="noscroll">
                <NavbarMaps />
                <div class="sidenav">
                    <div class="sidePlaceName">{this.state.targetProvince}</div>
                    <div class="sideClickedPlace">{this.state.clickedPlace}</div>

                </div>

                <Map google={this.props.google}
                    style={mapStyles}
                    zoom={10}
                    initialCenter={{lat: 13.407180, lng: 99.998090}}
                >
                    {this.state.placeResponse.map( thread => 
                        <div>
                            {thread.coor.map( c => (
                                <Marker key={thread.threadId}
                                    onClick={this.onMarkerClick}
                                    name={c.placeName}
                                    position={{lat: c.lat, lng: c.lon}} 
                                />
                            ))}
                        </div>
                    )}

                    {/* <Marker
                        onClick={this.onMarkerClick}
                        name={"test"}
                        position={{lat: 18.7749045, lng: 100.771892}} 
                    /> */}

                    <InfoWindow
                        marker={this.state.activeMarker}
                        visible={this.state.showingInfoWindow}>
                        <p>{this.state.selectedPlace.name}</p>
                    </InfoWindow>
                </Map>
            </div>
        );
    }
}

// export default MapContainer;

export default GoogleApiWrapper({
    // apiKey: ('AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM')
    apiKey: ('AIzaSyDDViAvlzx_1iSnaUKtNNLr-OuJpSeypjI')
})(MapContainer)
