// ============================= Google Maps TEST =============================
// AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM

import React, { Component } from 'react';
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import NavbarMaps from '../../components/common/navbarMaps';
import './maps.css'

const mapStyles = {
    top: '0',
    buttom: '0',
    width: 'auto',
    height: 'auto',
    left: '300px',
};

export class MapContainer extends Component {
    state = {
        showingInfoWindow: false,
        activeMarker: {},
        selectedPlace: {},
        placename: '',
    };

    onMarkerClick = (props, marker, e) =>
        this.setState({
            selectedPlace: props,
            activeMarker: marker,
            showingInfoWindow: true,
            placename: props.name,
        });

    onMapClicked = (props) => {
        if (this.state.showingInfoWindow) {
            this.setState({
                showingInfoWindow: false,
                activeMarker: null
            })
        }
    };

    render() {
        return (  
            <div class="noscroll">
                <NavbarMaps />
                <div class="sidenav">
                    <div class="sidePlaceName">ขอนแก่น</div>
                    <div class="sideClickedPlace">{this.state.placename}</div>
                    {this.state.placename === "ถ้ำพญานาค"? 
                        <>
                            <a href="https://pantip.com/topic/36367940">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">ตะลุยหนองคาย..เบิ่งบั้งไฟพญานาค</div>
                                </div>  
                            </a> 
                            <a href="https://pantip.com/topic/36235573">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">เที่ยว หนองคาย .. เที่ยวได้ 12 เดือน</div>
                                </div>  
                            </a> 

                        </>
                    :   (this.state.placename === "ชุมแพ"? 
                            <>
                                <a href="https://pantip.com/topic/38622677">
                                    <div class="sideLinkBox">
                                        <div class="sideLinkTitle">:: ROADTRIP THAILAND :: ขึ้นเหนือล่องใต้ไปกับรถ ECO CAR</div>
                                    </div>  
                                </a> 
                            </>

                    :  <></>
                    )}
                </div>

                <Map google={this.props.google}
                    style={mapStyles}
                    zoom={9}
                    initialCenter={{lat: 16.4419355, lng: 102.8359921}}>
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'ถ้ำพญานาค'}
                        position={{lat: 16.8140293, lng: 101.9566578}} />
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'ชุมแพ'}
                        position={{lat: 16.5807832, lng: 102.0946818}} />

                    <InfoWindow
                        marker={this.state.activeMarker}
                        visible={this.state.showingInfoWindow}>
                        <p>{this.state.selectedPlace.name}</p>
                    </InfoWindow>
                </Map>
            </div>
        );
    }
}

export default GoogleApiWrapper({
    // apiKey: ('AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM')
    apiKey: ('AIzaSyDDViAvlzx_1iSnaUKtNNLr-OuJpSeypjI')
})(MapContainer)
