import React, { Component } from 'react';
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import axios from "axios";
import NavbarMaps from '../../components/common/navbarMaps';
import './maps.css'

const mapStyles = {
    top: '0',
    buttom: '0',
    width: 'auto',
    height: 'auto',
    left: '300px',
};
class MapContainer extends Component {    
    state = {
        showingInfoWindow: false,
        activeMarker: {},
        selectedPlace: {},
        provinceResponse:[],        
        placeResponse:[],
        placeTarget:[],
        placeList:[], 
        coor:[],
        clickedPlace: '',
        targetProvince: '',
        threadId:'',
        threadTitle: '',
    };

    componentDidMount() {
        axios.get(`http://localhost:30011/getRankProvince`, {
            params: {
                provinceRank: 1
            }
        })
        .then(res => {
            const provinceResponse = res.data[0];
            this.setState({ provinceResponse });
            this.setState({ targetProvince: this.state.provinceResponse.provinceTh })

            // console.log(this.state.provinceResponse.provinceLat, this.state.provinceResponse.provinceLon);
            return axios.get(`http://localhost:30011/getThreadData`, {
                params: {
                    provinceTh: this.state.targetProvince
                }
            });
        })
        .then(res => {
            this.setState({ placeResponse: res.data });

            var tmp = []
            this.state.placeResponse.map(row => 
                row.coor.forEach(function(data){
                    tmp.push(data);
                })
            )
            this.setState({coor: tmp})
        })  
    }

    onMarkerClick = (props, marker, e) =>
    this.setState({
        selectedPlace: props,
        activeMarker: marker,
        showingInfoWindow: true,
        clickedPlace: props.name,
    });

    onMapClicked = (props) => {
        if (this.state.showingInfoWindow) {
            this.setState({
                showingInfoWindow: false,
                activeMarker: null
            })
        }
    };

    render() {
        return (  
            <div class="noscroll">
                <NavbarMaps />
                <div class="sidenav">
                    <div class="sidePlaceName">{this.state.targetProvince}</div>
                    <div class="sideClickedPlace">{this.state.clickedPlace}</div>

                    {this.state.placeResponse.map( thread => {
                        return thread.placeList.map( place => {
                            return place === this.state.clickedPlace ?
                                <a href={"https://pantip.com/topic/"+thread.threadId}>
                                    <div class="sideLinkBox">
                                        <div class="sideLinkTitle">
                                            {thread.threadTitle}
                                        </div>
                                    </div>  
                                </a>
                                : null
                        })
                    })}
                </div>

                <Map google={this.props.google}
                    style={mapStyles}
                    zoom={10}
                    // initialCenter={{
                    //     lat: this.state.provinceResponse.provinceLat, 
                    //     lng: this.state.provinceResponse.provinceLon
                    // }}
                >
                    {this.state.coor.map( place => {
                        return place.province === this.state.targetProvince ?
                            <Marker key={place.placeId}
                                onClick={this.onMarkerClick}
                                name={place.placeName}
                                position={{lat: place.lat,lng: place.lon}}/>
                            : null
                        }
                    )}

                    <InfoWindow
                        marker={this.state.activeMarker}
                        visible={this.state.showingInfoWindow}>
                        <p>{this.state.selectedPlace.name}</p>
                    </InfoWindow>
                </Map>
            </div>
        );
    }
}

export default GoogleApiWrapper({
    apiKey: ('AIzaSyDDViAvlzx_1iSnaUKtNNLr-OuJpSeypjI')
})(MapContainer)
