import React, { useState } from 'react'
import styled from 'styled-components'

export default function HideAppBar(props) {
  // const data = useStaticQuery(graphql`
  //   query {
  //     logo: file(relativePath: { eq: "logo.png" }) {
  //       childImageSharp {
  //         fixed(width: 142, height: 53) {
  //           ...GatsbyImageSharpFixed
  //         }
  //       }
  //     }
  //   }
  // `)

  const [username] = useState('poonnn')
  const [anchorEl, setAnchorEl] = React.useState(null)

  const handleClick = event => {
    setAnchorEl(event.currentTarget)
  }

  const handleClose = () => {
    setAnchorEl(null)
  }

  return (
    <>
      {
        <>
          <CssBaseline />
          <HideOnScroll {...props}>
            <CustomNavbar>
              <CustomToolbar>
                <BigContainer>
                  <LeftContainer>
                    <LogoLink to="/home">
                      {/* <Img fixed={data.logo.childImageSharp.fixed} alt="" /> */}
                      {/* <img src={Logo} alt="logo" width="55" height="55" /> */}
                    </LogoLink>
                    <HeaderText>
                      <SellerLink to="/sell">SELLER</SellerLink>
                    </HeaderText>
                  </LeftContainer>

                  <RightContainer>
                    <AccountIcon />
                    <Text>{username}</Text>

                    {props.isManageProd ? (
                      <>
                        <StyledIconButton
                          aria-controls="simple-menu"
                          aria-haspopup="true"
                          onClick={handleClick}
                        >
                          {/* <StyledImage src={DropDownIcon} /> */}
                        </StyledIconButton>

                        <Menu
                          id="simple-menu"
                          anchorEl={anchorEl}
                          keepMounted
                          open={Boolean(anchorEl)}
                          onClose={handleClose}
                        >
                          <DropdownManageProduct to={'manageproduct'}>
                            <CustomMenuItem onClick={handleClose}>
                              จัดการสินค้า
                            </CustomMenuItem>
                          </DropdownManageProduct>
                          <DropdownPurchaseListLink to='/purchaselist'>
                            <CustomMenuItem onClick={handleClose}>
                              ตรวจสอบรายการสั่งซื้อ
                            </CustomMenuItem>
                          </DropdownPurchaseListLink>
                          <DropdownPurchaseOrderLogLink to='/purchaseorderlog'>
                            <CustomMenuItem onClick={handleClose}>
                              ประวัติการขาย
                            </CustomMenuItem>
                          </DropdownPurchaseOrderLogLink>
                          <DropdownManageMember to={'managemember'}>
                            <CustomMenuItem onClick={handleClose}>
                              จัดการสมาชิกร้านค้า
                            </CustomMenuItem>
                          </DropdownManageMember>
                          <CustomMenuItem onClick={handleClose}>
                            แก้ไขข้อมูลร้านค้า
                          </CustomMenuItem>
                        </Menu>
                      </>
                    ) : null}

                    {props.notification === 0 ? (
                      <>
                        <NotiContainer>
                          <SmallNotificationIcon />
                        </NotiContainer>
                      </>
                    ) : (
                      <>
                        <NotiContainer>
                          <StyledBadge badgeContent={props.notification}>
                            <BigNotificationIcon />
                          </StyledBadge>
                        </NotiContainer>
                      </>
                    )}

                    <ButtonContainer>
                      <CustomButton variant="outlined">
                        แนะนำผู้ขาย
                      </CustomButton>
                    </ButtonContainer>
                  </RightContainer>
                </BigContainer>
              </CustomToolbar>
            </CustomNavbar>
          </HideOnScroll>

          <CustomToolbar />
          {/* <Container>
            {props.content}
        </Container> */}
        </>
      }
    </>
  )
}
