// ============================= Google Maps TEST =============================
// AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM

import React, { Component } from 'react';
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';

const mapStyles = {
  width: '80%',
  height: '80%',
  // position: 'relative'
};

export class MapContainer extends Component {
  state = {
    showingInfoWindow: false,
    activeMarker: {},
    selectedPlace: {},
  };

  onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });

  onMapClicked = (props) => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      })
    }
  };
  render() {
    return (      
      <Map google={this.props.google}
          style={mapStyles}
          className={'map'}
          zoom={11}
          initialCenter={{lat: 7.95193, lng: 98.3381}}>
          <Marker
            name={'หาดสุรินทร'}
            position={{lat: 7.97547, lng: 98.2785}} />
          <Marker
            name={'หาดกะรน'}
            position={{lat: 7.8439, lng: 98.2936}} />
          <Marker
            name={'วัดฉลอง'}
            position={{lat: 7.84707, lng: 98.3363}} />
          <Marker
            name={'แหลมพรหมเทพ'}
            position={{lat: 7.75886, lng: 98.3035}} />
        <Marker
          name={'Your position'}
          position={{lat: 7.95193, lng: 98.3381}}
          icon={{
            url: "/path/to/custom_icon.png",
            anchor: new window.google.maps.Point(32,32),
            scaledSize: new window.google.maps.Size(64,64)
          }} />
      </Map>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: ('AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM')
})(MapContainer)
