// ============================= Google Maps TEST =============================
// AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM

import React, { Component } from 'react';
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import NavbarMaps from '../../components/common/navbarMaps';
import './maps.css'

const mapStyles = {
    top: '0',
    buttom: '0',
    width: 'auto',
    height: 'auto',
    left: '300px',
};

export class MapContainer extends Component {
    state = {
        showingInfoWindow: false,
        activeMarker: {},
        selectedPlace: {},
        placename: '',
    };

    onMarkerClick = (props, marker, e) =>
        this.setState({
            selectedPlace: props,
            activeMarker: marker,
            showingInfoWindow: true,
            placename: props.name,
        });

    onMapClicked = (props) => {
        if (this.state.showingInfoWindow) {
            this.setState({
                showingInfoWindow: false,
                activeMarker: null
            })
        }
    };

    render() {
        return (  
            <div class="noscroll">
                <NavbarMaps />
                <div class="sidenav">
                    <div class="sidePlaceName">ปราจีนบุรี</div>
                    <div class="sideClickedPlace">{this.state.placename}</div>
                    {this.state.placename === "เมืองปราจีนบุรี"? 
                        <>
                            <a href="https://pantip.com/topic/36558364">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">"77 จังหวัด กับ ศาลหลักเมืองประเทศไทย".."สถานที่สำคัญ ที่เราไม่ควรลืม"</div>
                                </div>  
                            </a> 
                            <a href="https://pantip.com/topic/40102588">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">* * * ชวนเที่ยว น่าน – ปัว – สะปัน หลังหมด Covid (ในไทย) กันครับ * * *</div>
                                </div>  
                            </a> 
                            <a href="https://pantip.com/topic/39542285">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">** พาเที่ยวเหนือแบบไม่วุ่นวาย ในเทศกาลปีใหม่ครับ **</div>
                                </div>  
                            </a> 
                            <a href="https://pantip.com/topic/39059630">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">ราชบุรี one-day trip</div>
                                </div>  
                            </a> 

                        </>
                    :   (this.state.placename === "เมืองแก่งหิน"? 
                            <>
                                <a href="https://pantip.com/topic/36558364">
                                    <div class="sideLinkBox">
                                        <div class="sideLinkTitle">"77 จังหวัด กับ ศาลหลักเมืองประเทศไทย".."สถานที่สำคัญ ที่เราไม่ควรลืม"</div>
                                    </div>  
                                </a> 
                                <a href="https://pantip.com/topic/38951918">
                                    <div class="sideLinkBox">
                                        <div class="sideLinkTitle">บินเดี่ยวตะลุยเส้นทางเลาะเลียบแม่น้ำโขงร่วม 3,000 กม. ไปกับ Yamaha Exciter 150 2019</div>
                                    </div>  
                                </a> 

                            </>
                    :   (this.state.placename === ""? 
                        <>
                            <a href="https://pantip.com/topic/">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle"></div>
                                </div>  
                            </a> 
                        </>
                    :  <></>
                    ))}
                </div>

                <Map google={this.props.google}
                    style={mapStyles}
                    zoom={10}
                    initialCenter={{lat: 14.0420699, lng: 101.6600874}}>
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'เมืองปราจีนบุรี'}
                        position={{lat: 14.0509674, lng: 101.3727408}} />

                    <Marker
                        onClick={this.onMarkerClick}
                        name={'เมืองแก่งหิน'}
                        position={{lat: 14.1836108, lng: 101.7188961}} />
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'น้ำตกธารทิพย์'}
                        position={{lat: 14.197124, lng: 101.5432013}} />

                    <InfoWindow
                        marker={this.state.activeMarker}
                        visible={this.state.showingInfoWindow}>
                        <p>{this.state.selectedPlace.name}</p>
                    </InfoWindow>
                </Map>
            </div>
        );
    }
}

export default GoogleApiWrapper({
    apiKey: ('AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM')
})(MapContainer)
