        // axios.get(`http://localhost:30011/getRankPlaces?placeRank=1`)
        //     .then(res => {
        //         this.setState({ rank1 : res.data[0] });
        //     })
        axios.all([
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=1`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=2`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=3`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=4`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=5`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=6`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=7`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=8`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=9`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=10`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=11`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=12`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=13`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=14`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=15`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=16`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=17`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=18`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=19`),
            axios.get(`http://localhost:30011/getRankPlaces?placeRank=20`),

        ])    
        .then( res => {
            this.setState({rank1: res[0].data[0]});
        })
        console.log(this.state.rank1);
    }

    render() {
        return (
        <div class="top layout placeBox">
            <Navbar />
            <div class="BGimg" />
            <div class="LayOut">
                <div class="PageTitle">Popular Places</div>
                <div class="PageBreadCrumbs">Home&emsp;|&emsp;Popular Places</div>
                <div class="Title">Top 20  Places <br /> in Thailand</div>
                <div class="row">
    {/* Left column */}
                    <div class="column">
                        {/* <div class="TopPlaceContainer">
                            <div class="Picture01" />
                            <div class="TextBox">
                                <div class="RankTop">1 ST</div>
                                {this.state.rankResponse.map(data => data.placeRank===1? 
                                    <div class="PlaceName">{data.placeName}</div>
                                    :null)}
                            </div>
                            <StyledButton to="/top1Province" />                            
                        </div>

                        <div class="TopPlaceContainer">
                            <div class="Picture03" />
                            <div class="TextBox">
                                <div class="RankTop">3 RD</div>
                                {this.state.rankResponse.map( data => data.placeRank===3? 
                                    // <div class="PlaceName">{data.placeName}</div>
                                    <div class="PlaceName">Yep</div>
                                    :null)}
                            </div>
                            <StyledButton to="/top3Province" />
                        </div> */}
                        {/* {this.state.odd.map( rank => {
                            return <div class="TopPlaceContainer">
                                <div class="Picture03" />
                                <div class="TextBox">
                                    <div class="RankReg">{rank} TH</div>
                                    {this.state.rankResponse.map(data => data.placeRank===rank? 
                                        <div class="PlaceName">{data.placeName}</div>
                                        :null)
                                    }
                                </div>
                                <StyledButton to="/top3Province" />
                            </div>
                        })} */}

    {/* Right column */}
                    </div>
                    <div class="column">
                        {/* <div class="TopPlaceContainer">
                            <div class="Picture02" />
                            <div class="TextBox">
                                <div class="RankTop">2 ND</div>
                                {this.state.rankResponse.map(data => data.placeRank===2? 
                                    <div class="PlaceName">{data.placeName}</div>
                                    :null)
                                }
                            </div>
                            <StyledButton to="/top2Province" />
                        </div> */}
                        {/* {this.state.even.map( rank => {
                            return <div class="TopPlaceContainer">
                                <div class="Picture03" />
                                <div class="TextBox">
                                    <div class="RankReg">{rank} TH</div>
                                    {this.state.rankResponse.map(data => data.placeRank===rank? 
                                        <div class="PlaceName">{data.placeName}</div>
                                        :null)
                                    }
                                </div>
                                <StyledButton to="/top3Province" />
                            </div>
                        })} */}

                    </div>
                </div>
            </div>
        </div>
        );
    }
}
