// ============================= Google Maps TEST =============================
// AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM

import React, { Component } from 'react';
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import axios from "axios";
import NavbarMaps from '../../components/common/navbarMaps';
import './maps.css'

const mapStyles = {
    top: '0',
    buttom: '0',
    width: 'auto',
    height: 'auto',
    left: '300px',
};
class MapContainer extends Component {    
    state = {
        showingInfoWindow: false,
        activeMarker: {},
        selectedPlace: {},
        provinceResponse:[],        
        placeResponse:[],
        placeTarget:[],
        coor:{},
        clickedPlace: '',
        targetProvince: '',
        threadId:'',
        threadTitle: '',

    };

    componentDidMount() {
        axios.get(`http://localhost:30011/getRankProvince`, {
            params: {
                provinceRank: 1
            }
        })
            .then(res => {
                const provinceResponse = res.data[0];
                this.setState({ provinceResponse });
                this.setState({ targetProvince: this.state.provinceResponse.provinceTh })
                console.log(this.state.targetProvince);
        })

        axios.get(`http://localhost:30011/getThreadData`, {
            params: {
                // provinceTh: this.state.targetProvince
                provinceTh: "สมุทรสงคราม"
            }
        })
        .then(res => {
            this.setState({ placeResponse: res.data });
            console.log(this.state.placeResponse);

            var tmp = []
            this.state.placeResponse.map(row => 
                row.coor.forEach(function(data){
                    tmp.push(data);
                })
            )
            this.setState({coor: tmp})
            console.log(this.state.coor);
            console.log(this.state.coor[0]);            

        })  
    }

    onMarkerClick = (props, marker, e) =>
    this.setState({
        selectedPlace: props,
        activeMarker: marker,
        showingInfoWindow: true,
        clickedPlace: props.name,
    });

    onMapClicked = (props) => {
        if (this.state.showingInfoWindow) {
            this.setState({
                showingInfoWindow: false,
                activeMarker: null
            })
        }
    };

    render() {
        return (  
            <div class="noscroll">
                <NavbarMaps />
                <div class="sidenav">
                    <div class="sidePlaceName">{this.state.targetProvince}</div>
                    <div class="sideClickedPlace">{this.state.clickedPlace}</div>
                    {/* {this.state.coor.map( place => 
                        <li key={place.placeId}>{place.placeName}</li>
                    )} */}
                    {/* {this.state.placeResponse.map( thread => 
                        <li key={thread._id}>{thread.threadTitle}</li>
                    )} */}
                </div>

                <Map google={this.props.google}
                    style={mapStyles}
                    zoom={10}
                    initialCenter={{lat: 13.407180, lng: 99.998090}}
                >
                    {this.state.coor.map( place => 
                        <Marker 
                            key={place.placeId}
                            onClick={this.onMarkerClick}
                            name={place.placeName}
                            position={{
                                lat: place.lat, 
                                lng: place.lon}} 
                            />

                    )}

                    {/* <Marker
                        onClick={this.onMarkerClick}
                        name={"test"}
                        position={{lat: 13.407180, lng: 99.998090}} 
                    /> */}

                    <InfoWindow
                        marker={this.state.activeMarker}
                        visible={this.state.showingInfoWindow}>
                        <p>{this.state.selectedPlace.name}</p>
                    </InfoWindow>
                </Map>
            </div>
        );
    }
}

// export default MapContainer;

export default GoogleApiWrapper({
    // apiKey: ('AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM')
    apiKey: ('AIzaSyDDViAvlzx_1iSnaUKtNNLr-OuJpSeypjI')
})(MapContainer)
