import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import TextField from '@material-ui/core/TextField';
import styled from 'styled-components';
import Navbar from '../../components/common/navbarPage';
import SearchIcon from '../../images/LandingPage/03-search.svg';
import './home.css';

const SearchField = styled(TextField)`
    width:825px;
    margin-top: 12px;
    && {
        & .MuiFilledInput-input {
            padding: 16px 16px;
        }
        & .MuiFilledInput-root {
            position: relative;
            transition: background-color 200ms cubic-bezier(0.0, 0, 0.2, 1) 0ms;
            background-color: rgba(0, 0, 0, 0.09);
            border-radius: 2px;
        }
    }
`
const StyledButton = styled(Link)`
    width: 51px;
    height: 51px;
    margin-top: 12px;
    margin-left: 10px;
    border: 0;
    border-radius: 2px;
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: #5863F8;
    background-image: url(${SearchIcon});
    background-repeat: no-repeat;
    background-position: center;
    cursor: pointer;
`

class home extends Component {
    state = {
        searchTerm : '',
    }

    updateSearchTerm = (e) => {
        this.setState( {searchTerm: e.target.value} )
    }

    render(){
        return (
            <div class="home">
                <Navbar />
                <div class="BGimg"/>
                <div class="SearchSectionContainer" />
                <div class="SearchContainer">
                    <div class="SearchText">
                        Search By Province
                    </div>
                    <div class="FlexContainer">
                        <SearchField
                            id="Province-SearchField"
                            placeholder="Province"
                            InputLabelProps={{shrink: true,}}
                            variant="filled"
                            onChange={this.updateSearchTerm}
                            value={this.state.searchTerm}
                        />
                        <StyledButton to={`/searchMap/${this.state.searchTerm}`}/>
                    </div>
                </div>
            </div>
        );
    }
}

export default home;
