// ============================= Google Maps TEST =============================
// AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM

import React, { Component } from 'react';
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import NavbarMaps from '../../components/common/navbarMaps';
import './maps.css'

const mapStyles = {
    top: '0',
    buttom: '0',
    width: 'auto',
    height: 'auto',
    left: '300px',
};

export class MapContainer extends Component {
    state = {
        showingInfoWindow: false,
        activeMarker: {},
        selectedPlace: {},
        placename: '',
    };

    onMarkerClick = (props, marker, e) =>
        this.setState({
            selectedPlace: props,
            activeMarker: marker,
            showingInfoWindow: true,
            placename: props.name,
        });

    onMapClicked = (props) => {
        if (this.state.showingInfoWindow) {
            this.setState({
                showingInfoWindow: false,
                activeMarker: null
            })
        }
    };

    render() {
        return (  
            <div class="noscroll">
                <NavbarMaps />
                <div class="sidenav">
                    <div class="sidePlaceName">นครนายก</div>
                    <div class="sideClickedPlace">{this.state.placename}</div>
                    {this.state.placename === "น้ำตกวังตะไคร้"? 
                        <>
                            <a href="https://pantip.com/topic/40019133">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">บ้านสวนในฝัน Villa Panalai นครนายก</div>
                                </div>  
                            </a> 
                        </>
                    :   (this.state.placename === "อุโมงต้นไผ่วัดจุฬาภรณ์วนาราม"? 
                            <>
                                <a href="https://pantip.com/topic/40019133">
                                    <div class="sideLinkBox">
                                        <div class="sideLinkTitle">บ้านสวนในฝัน Villa Panalai นครนายก</div>
                                    </div>  
                                </a> 

                            </>
                    :   (this.state.placename === "อ่างเก็บน้ำคลองโบด"? 
                        <>
                            <a href="https://pantip.com/topic/">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle"></div>
                                </div>  
                            </a> 
                        </>
                    :   (this.state.placename === "อ่างเก็บน้ำห้วยปรือ"? 
                        <>
                            <a href="https://pantip.com/topic/">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle"></div>
                                </div>  
                            </a> 
                        </>

                    :  <></>
                    )))}
                </div>

                <Map google={this.props.google}
                    style={mapStyles}
                    zoom={10}
                    initialCenter={{lat: 14.206946599999998, lng: 101.2130511}}>
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'น้ำตกวังตะไคร้'}
                        position={{lat: 14.3356255, lng: 101.3061883}} />
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'อุโมงต้นไผ่วัดจุฬาภรณ์วนาราม'}
                        position={{lat: 14.278518900000002, lng: 101.0325697}} />
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'อ่างเก็บน้ำคลองโบด'}
                        position={{lat: 14.2865358, lng: 101.1919006}} />
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'อ่างเก็บน้ำห้วยปรือ'}
                        position={{lat: 14.3097157, lng: 101.2132303}} />

                    <InfoWindow
                        marker={this.state.activeMarker}
                        visible={this.state.showingInfoWindow}>
                        <p>{this.state.selectedPlace.name}</p>
                    </InfoWindow>
                </Map>
            </div>
        );
    }
}

export default GoogleApiWrapper({
    apiKey: ('AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM')
})(MapContainer)
