// ============================= Google Maps TEST =============================
// AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM

import React, { Component } from 'react';
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import NavbarMaps from '../../components/common/navbarMaps';
import './maps.css'

const mapStyles = {
    top: '0',
    buttom: '0',
    width: 'auto',
    height: 'auto',
    left: '300px',
};

export class MapContainer extends Component {
    state = {
        showingInfoWindow: false,
        activeMarker: {},
        selectedPlace: {},
        placename: '',
    };

    onMarkerClick = (props, marker, e) =>
        this.setState({
            selectedPlace: props,
            activeMarker: marker,
            showingInfoWindow: true,
            placename: props.name,
        });

    onMapClicked = (props) => {
        if (this.state.showingInfoWindow) {
            this.setState({
                showingInfoWindow: false,
                activeMarker: null
            })
        }
    };

    render() {
        return (  
            <div class="noscroll">
                <NavbarMaps />
                <div class="sidenav">
                    <div class="sidePlaceName">พะเยา</div>
                    <div class="sideClickedPlace">{this.state.placename}</div>
                    {this.state.placename === "ดอยภูนม"? 
                        <>
                            <a href="https://pantip.com/topic/36100794">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">ไปปีน ภูลังกา นอนที่ ปังค่า ประทับใจมากครับ เป็นอีกอุทยานแห่งชาติที่งดงามมาก ๆ ..</div>
                                </div>  
                            </a> 
                        </>
                    :   (this.state.placename === "กว๊านพะเยา"? 
                            <>
                                <a href="https://pantip.com/topic/39542285">
                                    <div class="sideLinkBox">
                                        <div class="sideLinkTitle">** พาเที่ยวเหนือแบบไม่วุ่นวาย ในเทศกาลปีใหม่ครับ **</div>
                                    </div>  
                                </a> 
                            </>
                    :   (this.state.placename === "เชียงคำ"? 
                        <>
                            <a href="https://pantip.com/topic/39542285">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">** พาเที่ยวเหนือแบบไม่วุ่นวาย ในเทศกาลปีใหม่ครับ **</div>
                                </div>  
                            </a> 
                        </>
                    :   (this.state.placename === "วัดพระนั่งดิน"? 
                        <>
                            <a href="https://pantip.com/topic/39542285">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">** พาเที่ยวเหนือแบบไม่วุ่นวาย ในเทศกาลปีใหม่ครับ **</div>
                                </div>  
                            </a> 
                        </>
                    :   (this.state.placename === "อ.เชียงม่วน"? 
                        <>
                            <a href="https://pantip.com/topic/39542285">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">** พาเที่ยวเหนือแบบไม่วุ่นวาย ในเทศกาลปีใหม่ครับ **</div>
                                </div>  
                            </a> 
                        </>
                    :   (this.state.placename === "1221 บ้านทราย ต.เวียง อ.เชียงคำ จ.พะเยา"? 
                    <>
                        <a href="https://pantip.com/topic/39365201">
                            <div class="sideLinkBox">
                                <div class="sideLinkTitle">[GoaroundwithYou] ทริปเชียงราย-พะเยา พาแม่แอ่วเหนือ นอนโฮมสเตย์</div>
                            </div>  
                        </a> 
                    </>
                    :   (this.state.placename === "ต.ผาช้างน้อย อ.ปง จ.พะเยา"? 
                    <>
                        <a href="https://pantip.com/topic/39365201">
                            <div class="sideLinkBox">
                                <div class="sideLinkTitle">[GoaroundwithYou] ทริปเชียงราย-พะเยา พาแม่แอ่วเหนือ นอนโฮมสเตย์</div>
                            </div>  
                        </a> 
                    </>
                    :  <></>
                    ))))))}
                </div>

                <Map google={this.props.google}
                    style={mapStyles}
                    zoom={10}
                    initialCenter={{lat: 19.2154367, lng: 100.2023692}}>
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'ดอยภูนม'}
                        position={{lat: 19.394884, lng: 100.4779303}} />
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'กว๊านพะเยา'}
                        position={{lat: 19.166667, lng: 99.866667}} />
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'เชียงคำ'}
                        position={{lat: 19.4987437, lng: 100.3251564}} />
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'วัดพระนั่งดิน'}
                        position={{lat: 19.4984512, lng: 100.3241196}} />
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'อ.เชียงม่วน'}
                        position={{lat: 18.9475947, lng: 100.3048897}} />
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'1221 บ้านทราย ต.เวียง อ.เชียงคำ จ.พะเยา'}
                        position={{lat: 19.4976329, lng: 100.3271716}} />
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'ต.ผาช้างน้อย อ.ปง จ.พะเยา'}
                        position={{lat: 19.3233565, lng: 100.4547991}} />

                    <InfoWindow
                        marker={this.state.activeMarker}
                        visible={this.state.showingInfoWindow}>
                        <p>{this.state.selectedPlace.name}</p>
                    </InfoWindow>
                </Map>
            </div>
        );
    }
}

export default GoogleApiWrapper({
    // apiKey: ('AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM')
    apiKey: ('AIzaSyDDViAvlzx_1iSnaUKtNNLr-OuJpSeypjI')
})(MapContainer)
