// ============================= Google Maps TEST =============================
// AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM

import React, { Component } from 'react';
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import axios from "axios";
import NavbarMaps from '../../components/common/navbarMaps';
import './maps.css'

const mapStyles = {
    top: '0',
    buttom: '0',
    width: 'auto',
    height: 'auto',
    left: '300px',
};
class MapContainer extends Component {    
    state = {
        showingInfoWindow: false,
        activeMarker: {},
        selectedPlace: {},
        provinceResponse:[],        
        placeResponse:[],
        placeTarget:[],
        latLon:[],
        clickedPlace: '',
        targetProvince: '',
        targetPlace: '',
    };

    componentDidMount() {
        axios.get(`http://localhost:30011/getRankProvince`)
            .then(res => {
                const provinceResponse = res.data;
                this.setState({ provinceResponse });

                const target = this.state.provinceResponse.map(data =>
                    data.provinceRank === 1 ? data.provinceTh : null
                )
                this.setState({targetProvince:target})
                console.log(this.state.targetProvince);
        })

        // axios.get(`http://localhost:30011/getThreadData?${this.state.targetProvince}`)
        axios.get(`http://localhost:30011/getThreadData`, {
            params: {
                provinceTh: this.state.targetProvince
            }
        })
        .then(res => {
            // const placeResponse = res.data;
            console.log(res.data)
            this.setState({ placeResponse: res.data });

            // const placeTarget = this.state.placeResponse.map(data =>

            // )
            // this.setState({placeTarget});

            // const target = this.state.placeResponse.map( data => 
            //     data.provinceTh === this.state.targetProvince ? data.latLon : null
            // )
            // this.setState({latLon:target});
        })
    }

    onMarkerClick = (props, marker, e) =>
    this.setState({
        selectedPlace: props,
        activeMarker: marker,
        showingInfoWindow: true,
        clickedPlace: props.name,
    });

    onMapClicked = (props) => {
        if (this.state.showingInfoWindow) {
            this.setState({
                showingInfoWindow: false,
                activeMarker: null
            })
        }
    };

    render() {

        return (  
            <div class="noscroll">
                <NavbarMaps />
                <div class="sidenav">
                    <div class="sidePlaceName">{this.state.targetProvince}</div>
                    <div class="sideClickedPlace">{this.state.clickedPlace}</div>
                    <h4>{this.state.placeResponse}</h4>

                </div>

                {/* <Map google={this.props.google}
                    style={mapStyles}
                    zoom={10}
                    initialCenter={{lat: 18.7756318, lng: 100.7730417}}
                >
                    {this.state.placeTarget.map( data => {
                        return(
                            <Marker
                                onClick={this.onMarkerClick}
                                name={data}
                                position={{lat: 18.7749045, lng: 100.771892}} 
                            />
                        )}
                    )}

                    <InfoWindow
                        marker={this.state.activeMarker}
                        visible={this.state.showingInfoWindow}>
                        <p>{this.state.selectedPlace.name}</p>
                    </InfoWindow>
                </Map> */}
            </div>
        );
    }
}

// export default MapContainer;

export default GoogleApiWrapper({
    // apiKey: ('AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM')
