import React, { Component } from "react";
import axios from "axios";

export default class Details extends Component {
  constructor() {
    super();
    this.state = {
      data: [],
      buttonClicked: false
    };
    this.getDetails = this.getDetails.bind(this);
  }

  componentDidMount() {
    axios.get("http://localhost:4000/getData").then(res => {
      this.setState({
        data: res.data
      });
    });
  }

  getDetails() {
    if (!this.state.buttonClicked) {
      this.setState({
        buttonClicked: true
      });
    }
  }

  render() {
    return (
      <div >
        <div >
          <div >
            <button onClick={this.getDetails}>
              Click
            </button>
            <div>
              {this.state.buttonClicked
                ? this.state.data.map.orderBy(['provinceRank'], 'asc')(data => {
                    return (
                      <React.Fragment>
                        <p>
                          {" "}
                          <b>Province Name</b> : {data.provinceEn}
                        </p>
                        <p>
                          <b>Desc</b> : {data.provinceDesc}
                        </p>
                        <hr />
                      </React.Fragment>
                    );
                  })
                : null}
            </div>
          </div>
        </div>
      </div>
    );
  }
}
