import React, { useState } from 'react';
import {Link} from 'react-router-dom';
import arrow from '../../images/TopProvince/12_arrow-right.png';
import styled from 'styled-components';
// import TopProvinceComponents from '../container/TopProvinceComponents';

const StyledButton = styled(Link)`
    width: 60px;
    height: 100%;
    background-color: #5863F8;
    color: white;
    border: 0;
    display: flex;
    justify-content: center;
    align-items: center;
    cursor: pointer;
`
const [state, setstate] = useState({data:""}) 

const changeState = () => {   
  setstate({
    data: props.data
  })
}; 

export default function NextButton(props) {
  return (
      <StyledButton to="/maps" onClick={changeState}>
        {this.state.data = props}

        {/* {this.state.province.map(data => data.provinceRank===1? 
                                  <>
                                    <div class="PlaceName">{data.provinceTh}</div>
                                    <div class="PlaceDesc">{data.provinceDesc}</div>
                                  </>
                                  :null)} */}

        <img src={arrow} alt="arrow_next" />
      </StyledButton>
  );
}
