// ============================= Google Maps TEST =============================
// AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM

import React, { Component } from 'react';
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import NavbarMaps from '../../components/common/navbarMaps';
import './maps.css'

const mapStyles = {
    top: '0',
    buttom: '0',
    width: 'auto',
    height: 'auto',
    left: '300px',
};

export class MapContainer extends Component {
    state = {
        showingInfoWindow: false,
        activeMarker: {},
        selectedPlace: {},
        placename: '',
    };

    onMarkerClick = (props, marker, e) =>
        this.setState({
            selectedPlace: props,
            activeMarker: marker,
            showingInfoWindow: true,
            placename: props.name,
        });

    onMapClicked = (props) => {
        if (this.state.showingInfoWindow) {
            this.setState({
                showingInfoWindow: false,
                activeMarker: null
            })
        }
    };

    render() {
        return (  
            <div class="noscroll">
                <NavbarMaps />
                <div class="sidenav">
                    <div class="sidePlaceName">ตรัง</div>
                    <div class="sideClickedPlace">{this.state.placename}</div>
                    {this.state.placename === "เกาะรอก"? 
                        <>
                            <a href="https://pantip.com/topic/39893410">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">13 เกาะ ทะเลไทย สวยไม่แพ้ชาติใดในโลก</div>
                                </div>  
                            </a> 
                        </>
                    :   (this.state.placename === "ถ้ำมรกต"? 
                            <>
                                <a href="https://pantip.com/topic/36143805">
                                    <div class="sideLinkBox">
                                        <div class="sideLinkTitle">10 ทะเลไทยน่าเที่ยว</div>
                                    </div>  
                                </a> 
                                <a href="https://pantip.com/topic/36753313">
                                    <div class="sideLinkBox">
                                        <div class="sideLinkTitle">13 ทะเลไทย หัวหิน พัทยา สมุย อันดามัน หลีเป๊ะ สิมิลัน พีพี ทะเลแหวก ฯลฯ ช่วงไหนควรไปทะเล พร้อมข้อมูลรีวิว</div>
                                    </div>  
                                </a> 
                            </>
                    :   (this.state.placename === "สิเกา"? 
                        <>
                            <a href="https://pantip.com/topic/36753313">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">13 ทะเลไทย หัวหิน พัทยา สมุย อันดามัน หลีเป๊ะ สิมิลัน พีพี ทะเลแหวก ฯลฯ ช่วงไหนควรไปทะเล พร้อมข้อมูลรีวิว</div>
                                </div>  
                            </a> 
                        </>
                    :   (this.state.placename === "หาดปากเมง"? 
                        <>
                            <a href="https://pantip.com/topic/36753313">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">13 ทะเลไทย หัวหิน พัทยา สมุย อันดามัน หลีเป๊ะ สิมิลัน พีพี ทะเลแหวก ฯลฯ ช่วงไหนควรไปทะเล พร้อมข้อมูลรีวิว</div>
                                </div>  
                            </a> 
                            <a href="https://pantip.com/topic/36143805">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">10 ทะเลไทยน่าเที่ยว</div>
                                </div>  
                            </a> 
                        </>
                    :   (this.state.placename === "เกาะมุก"? 
                        <>
                            <a href="https://pantip.com/topic/36143805">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">10 ทะเลไทยน่าเที่ยว</div>
                                </div>  
                            </a> 
                        </>
                    :   (this.state.placename === "เกาะกระดาน"? 
                        <>
                            <a href="https://pantip.com/topic/36143805">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">10 ทะเลไทยน่าเที่ยว</div>
                                </div>  
                            </a> 
                        </>
                    :   (this.state.placename === "เกาะเชือก"? 
                        <>
                            <a href="https://pantip.com/topic/36143805">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">10 ทะเลไทยน่าเที่ยว</div>
                                </div>  
                            </a> 
                        </>

                    :  <></>
                    ))))))}
                </div>

                <Map google={this.props.google}
                    style={mapStyles}
                    zoom={10}
                    initialCenter={{lat: 7.559385099999999, lng: 99.6110065}}>
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'เกาะรอก'}
                        position={{lat: 7.199584199999999, lng: 99.0598732}} />
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'ถ้ำมรกต'}
                        position={{lat: 7.369089, lng: 99.283746}} />
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'สิเกา'}
                        position={{lat: 7.572654299999999, lng: 99.34382959999999}} />
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'หาดปากเมง'}
                        position={{lat: 7.4948668, lng: 99.3268695}} />
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'เกาะมุก'}
                        position={{lat: 7.370857000000001, lng: 99.2979641}} />
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'เกาะกระดาน'}
                        position={{lat: 7.314398, lng: 99.2522292}} />
                    <Marker
                        onClick={this.onMarkerClick}
                        name={'เกาะเชือก'}
                        position={{lat: 7.4059808, lng: 99.2311029}} />

                    <InfoWindow
                        marker={this.state.activeMarker}
                        visible={this.state.showingInfoWindow}>
                        <p>{this.state.selectedPlace.name}</p>
                    </InfoWindow>
                </Map>
            </div>
        );
    }
}

export default GoogleApiWrapper({
    // apiKey: ('AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM')
    apiKey: ('AIzaSyDDViAvlzx_1iSnaUKtNNLr-OuJpSeypjI')
})(MapContainer)
