// ============================= Google Maps TEST =============================
// AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM

import React, { Component } from 'react';
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import NavbarMaps from '../../components/common/navbarMaps';
import './maps.css'

const mapStyles = {
    top: '0',
    buttom: '0',
    width: 'auto',
    height: 'auto',
    left: '300px',
};

export class MapContainer extends Component {
    state = {
        showingInfoWindow: false,
        activeMarker: {},
        selectedPlace: {},
        placename: '',
    };

    onMarkerClick = (props, marker, e) =>
        this.setState({
            selectedPlace: props,
            activeMarker: marker,
            showingInfoWindow: true,
            placename: props.name,
        });

    onMapClicked = (props) => {
        if (this.state.showingInfoWindow) {
            this.setState({
                showingInfoWindow: false,
                activeMarker: null
            })
        }
    };

    render() {
        return (  
            <div class="noscroll">
                <NavbarMaps />
                <div class="sidenav">
                    <div class="sidePlaceName">ตรัง</div>
                    <div class="sideClickedPlace">{this.state.placename}</div>
                    {this.state.placename === "เกาะรอก"? 
                        <>
                            <a href="https://pantip.com/topic/39893410">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">13 เกาะ ทะเลไทย สวยไม่แพ้ชาติใดในโลก</div>
                                </div>  
                            </a> 
                        </>
                    :   (this.state.placename === "ถ้ำมรกต"? 
                            <>
                                <a href="https://pantip.com/topic/36143805">
                                    <div class="sideLinkBox">
                                        <div class="sideLinkTitle">10 ทะเลไทยน่าเที่ยว</div>
                                    </div>  
                                </a> 
                                <a href="https://pantip.com/topic/36753313">
                                    <div class="sideLinkBox">
                                        <div class="sideLinkTitle">13 ทะเลไทย หัวหิน พัทยา สมุย อันดามัน หลีเป๊ะ สิมิลัน พีพี ทะเลแหวก ฯลฯ ช่วงไหนควรไปทะเล พร้อมข้อมูลรีวิว</div>
                                    </div>  
                                </a> 
                            </>
                    :   (this.state.placename === "สิเกา"? 
                        <>
                            <a href="https://pantip.com/topic/36753313">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">13 ทะเลไทย หัวหิน พัทยา สมุย อันดามัน หลีเป๊ะ สิมิลัน พีพี ทะเลแหวก ฯลฯ ช่วงไหนควรไปทะเล พร้อมข้อมูลรีวิว</div>
                                </div>  
                            </a> 
                        </>
                    :   (this.state.placename === "หาดปากเมง"? 
                        <>
                            <a href="https://pantip.com/topic/36753313">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">13 ทะเลไทย หัวหิน พัทยา สมุย อันดามัน หลีเป๊ะ สิมิลัน พีพี ทะเลแหวก ฯลฯ ช่วงไหนควรไปทะเล พร้อมข้อมูลรีวิว</div>
                                </div>  
                            </a> 
                            <a href="https://pantip.com/topic/36143805">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">10 ทะเลไทยน่าเที่ยว</div>
                                </div>  
                            </a> 
                        </>
                    :   (this.state.placename === "เกาะมุก"? 
                        <>
                            <a href="https://pantip.com/topic/36143805">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">10 ทะเลไทยน่าเที่ยว</div>
                                </div>  
                            </a> 
                        </>
                    :   (this.state.placename === "เกาะกระดาน"? 
                        <>
                            <a href="https://pantip.com/topic/36143805">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">10 ทะเลไทยน่าเที่ยว</div>
                                </div>  
                            </a> 
                        </>
                    :   (this.state.placename === "เกาะเชือก"? 
                        <>
                            <a href="https://pantip.com/topic/36143805">
                                <div class="sideLinkBox">
                                    <div class="sideLinkTitle">10 ทะเลไทยน่าเที่ยว</div>
                                </div>  
                            </a> 
                        </>

                    :  <></>
                    ))))))}
                </div>

                <Map google={this.props.google}
