// ============================= Google Maps TEST =============================
// AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM

import React, { Component } from 'react';
import {Map, InfoWindow, Marker, GoogleApiWrapper} from 'google-maps-react';
import NavbarMaps from '../../components/common/navbarMaps';
import '../../components/common/SideNav.css'
import styled from 'styled-components';

const mapStyles = {
    top: '0',
    buttom: '0',
    width: '1140px',
    height: '725px',
    left: '300px',
};

const sidePlaceName = styled.div`

`

export class MapContainer extends Component {
    state = {
        showingInfoWindow: false,
        activeMarker: {},
        selectedPlace: {},
    };

onMarkerClick = (props, marker, e) =>
    this.setState({
        selectedPlace: props,
        activeMarker: marker,
        showingInfoWindow: true
    });

onMapClicked = (props) => {
    if (this.state.showingInfoWindow) {
        this.setState({
            showingInfoWindow: false,
            activeMarker: null
        })
    }
};

render() {
    return (  
        <>
            <NavbarMaps />
            <div class="sidenav">
                <a href="/">Home</a>
                <a href="/province">Province</a>
                <a href="/maps">Maps</a>
            </div>

            <Map google={this.props.google}
                style={mapStyles}
                zoom={11}
                initialCenter={{lat: 7.95193, lng: 98.3381}}>
                <Marker
                onClick={this.onMarkerClick}
                name={'หาดสุรินทร์'}
                url={'https://pantip.com/topic/40091067'}
                position={{lat: 7.97547, lng: 98.2785}} />
                <Marker
                onClick={this.onMarkerClick}
                name={'หาดกะรน'}
                url={'https://pantip.com/topic/39936903'}
                position={{lat: 7.8439, lng: 98.2936}} />
                <Marker
                onClick={this.onMarkerClick}
                name={'วัดฉลอง'}
                url={'https://pantip.com/topic/40207039'}
                position={{lat: 7.84707, lng: 98.3363}} />
                <Marker
                onClick={this.onMarkerClick}
                name={'แหลมพรหมเทพ'}
                url={'https://pantip.com/topic/40155571'}
                position={{lat: 7.75886, lng: 98.3035}} />

                <InfoWindow
                marker={this.state.activeMarker}
                visible={this.state.showingInfoWindow}>
                <div>
                    <a href={this.state.selectedPlace.url}>
                    <p>{this.state.selectedPlace.name}</p>
                    </a>
                </div>
                </InfoWindow>
            </Map>
        </>
    );
}
}

export default GoogleApiWrapper({
    apiKey: ('AIzaSyCoOZKGkK9Ili7RHCrB0dVNqhy6_e1yKyM')
})(MapContainer)
